          type="PX.Data.PXActiveDirectorySyncMembershipProvider, PX.Data" 
             mainProviderType="PX.Data.PXDatabaseMembershipProvider" . . . /&gt;
     &lt;/providers&gt;
 &lt;/membership&gt;
}}}}
