    {
        ReturnBehavior = ReturnBehavior.OnlySpecified,
        ID = processResult.EntityId,
        Shipments = new SalesOrderShipment[]
        {
            new SalesOrderShipment { ReturnBehavior = ReturnBehavior.All }
        }
    });

