row.column:
add screen AM300030 {
  openAs = Form
  add container "HeaderView" {
    add layout "HeaderLayout" {
      displayName = "HeaderLayout"
      layout = "HeaderSticky"
      add field "ReferenceNbr"
      add field "Warehouse"
    }
    add layout "FooterLayout" {
      displayName = "FooterLayout"
      layout = "FooterSticky"
      add layout "Line1" {
        displayName = "Line1"
        layout = "Inline"
        add recordActionLink "Review"
        add recordActionLink "ClearBtn"
        add recordActionLink "Release"
        add recordActionLink "ScanBarcode"
      }
      add layout "Line2" {
        displayName = "Line2"
        layout = "Inline"
        add recordActionLink "ScanRemove"
        add recordActionLink "ScanQty"
        add recordActionLink "ScanConfirm"
      }
      add group "ChangeModeGroup" {
        displayName = "Change Mode"
        collapsable = True
        collapsed = True
        add layout "Line3" {
          displayName = "Line3"
          layout = "Inline"
          add recordActionLink "ScanModePick"
          add recordActionLink "ScanModePack"
          add recordActionLink "ScanModeShip"
        }
        add layout "Line4" {
          displayName = "Line4"
          layout = "Inline"
          add recordActionLink "ScanModeReceive"
          add recordActionLink "ScanModePutAway"
        }
        add layout "Line5" {
          displayName = "Line5"
          layout = "Inline"
          add recordActionLink "ScanModeIssue"
          add recordActionLink "ScanModeInReceive"
          add recordActionLink "ScanModeInTransfer"
        }
        add layout "Line6" {
          displayName = "Line6"
          layout = "Inline"
          add recordActionLink "ScanModePhysicalCount"
          add recordActionLink "ScanModeItemLookup"
          add recordActionLink "ScanModeStoragePlaceLookup"
        }
      }
    }
    add field "Mode#Message" {
      displayName = " "
      forceIsVisible = True
      textType = PlainMultiLine
      formPriority = 98
    }
    add field "Scan" {
      special = BarCodeScan
    }
    add field "Mode#MessageSound" {
      special = SoundAlert
    }
    add recordAction "ScanRelease" {
      syncLongOperation = True
    }
    add recordAction "Save" {
      behavior = Save
    }
    add recordAction "Cancel" {
      behavior = Cancel
    }
    add recordAction "ClearBtn"
    add recordAction "ScanConfirm"
    add recordAction "ScanRemove"
    add recordAction "ScanQty"
    add recordAction "Review" {
      displayName = "Review"
      behavior = Void
      redirect = True
      redirectToContainer = "Issue$List"
    }
    add recordAction "ScanBarcode" {
      displayName = "Scan"
      behavior = BarCodeCameraScan
    }
    add recordAction "ScanModePick" {
      redirect = True
    }
    add recordAction "ScanModePack" {
      redirect = True
    }
    add recordAction "ScanModeShip" {
      redirect = True
    }
    add recordAction "ScanModeReceive" {
      redirect = True
    }
    add recordAction "ScanModePutAway" {
      redirect = True
    }
    add recordAction "ScanModeItemLookup" {
      redirect = True
    }
    add recordAction "ScanModeStoragePlaceLookup" {
      redirect = True
    }
    add recordAction "ScanModeIssue" {
      redirect = True
    }
    add recordAction "ScanModeInReceive" {
      redirect = True
    }
    add recordAction "ScanModeInTransfer" {
      redirect = True
    }
    add recordAction "ScanModePhysicalCount" {
      redirect = True
    }
    attachments {
      add type "jpg" {
        extension = "jpg"
      }
      add type "png" {
        extension = "png"
      }
      add type "pdf" {
        extension = "pdf"
      }
    }
  }
  add container "Issue" {
    displayName = "Items"
    visible = False
    fieldsToShow = 3
    add field "InventoryID" {
      forceIsDisabled = True
      listPriority = 100
    }
    add field "Description" {
      forceIsDisabled = True
      listDisplayFormat = CaptionValue
    }
    add field "Location" {
      listPriority = 80
      listDisplayFormat = CaptionValue
    }
    add layout "Line_Qty_UOM" {
      displayName = "Line_Qty_UOM"
      layout = "Inline"
      add field "Quantity" {
        forceIsDisabled = True
        listPriority = 90
      }
      add field "UOM" {
        forceIsDisabled = True
        listDisplayFormat = CaptionValue
      }
    }
    add layout "Line_Lot_ExpireDate" {
      displayName = "Line_Lot_ExpireDate"
      layout = "Inline"
      add field "LotSerialNbr" {
        listDisplayFormat = CaptionValue
      }
      add field "ExpirationDate" {
        listDisplayFormat = CaptionValue
      }
    }
    add field "ReasonCode" {
      listDisplayFormat = CaptionValue
    }
    add recordAction "Delete" {
      icon = "system://Trash"
      behavior = Delete
      after = Close
    }
    add selectionAction "Delete" {
      icon = "system://Trash"
      behavior = Delete
    }
    attachments {
    }
  }
}

