        selectorDisplayFormat="KeyDescription"
	  }
      add field "OperationID"
      add field "Shift"
      add field "Quantity"
      add field "UOM"
      add field "StartTime"
      add field "Duration"
      add layout "FooterLayout" {
        displayName = "FooterLayout"
        layout = "FooterSticky"
        add layout "Line1" {
          displayName = "Line1"
          layout = "Inline"
          add recordActionLink "ClockInOut"
        }
      }
      add recordAction "ClockInOut"
      add containerAction "Insert" {
        icon = "system://Plus"
        behavior = Create
      }
      attachments {
        disabled = True
      }
    }
}

